SELECT exchange, CASE address WHEN from_address THEN 'out' ELSE 'in' END AS io, tx.* FROM `bigquery-public-data.crypto_ethereum.transactions` AS tx, `exchange-flow-demo.exchange_wallet_addresses.exchange_hotwallet_addresses` AS addr WHERE TRUE AND block_timestamp >= "2019-10-23 00:00:00" AND block_timestamp < "2019-10-24 00:00:00" AND (tx.from_address = addr.address OR tx.to_address = addr.address)
